import React from "react";
import Typography from "@material-ui/core/Typography";
import Avatar from "@material-ui/core/Avatar";
import Grid from "@material-ui/core/Grid";
import Box from "@material-ui/core/Box";
import Typed from "react-typed";
import { makeStyles } from "@material-ui/core/styles";
import avatar from "../avatar.png";

const useStyles = makeStyles((theme) => ({
  appbar: {
    background: "black", // Change the background color to black
    margin: 0,
  },
  arrow: {
    color: "tomato",
  },
  title: {
    color: "white",
  },
  menuSliderContainer: {
    width: 250,
    background: "#511",
    height: "100%",
  },
  avatar: {
    display: "block",
    margin: "0.5rem auto",
    width: theme.spacing(13),
    height: theme.spacing(13),
  },
  listItem: {
    color: "tan",
  },
}));


const Header = () => {
  const classes = useStyles();

  return (
    <Box className={classes.typedContainer}>
      {/* <Grid container justify="center">
        <Avatar className={classes.avatar} src={avatar} alt="Elizabeth (Izzie) Njeri" />
      </Grid> */}
 <Typography className={classes.title} variant="h4" style={{ color: 'white' }}>
      <Typed strings={["Elizabeth (Izzie) Njeri"]} typeSpeed={40} />
    </Typography>

      <Typography className={classes.subtitle} variant="h5">
        <Typed
          strings={[
            "Frontend Engineer",
            "Backend Engineer",
            "Native App Engineer",
          ]}
          typeSpeed={40}
          backSpeed={50}
          loop
        />
      </Typography>
    </Box>
  );
};

export default Header;
