import React from "react";
import Typography from "@material-ui/core/Typography";
import Avatar from "@material-ui/core/Avatar";
import Grid from "@material-ui/core/Grid";
import Box from "@material-ui/core/Box";
import { makeStyles } from "@material-ui/core/styles";
import avatar from "../avatar.png";
import Particles from "./Particles";

const useStyles = makeStyles((theme) => ({
    avatar: {
      width: theme.spacing(15),
      height: theme.spacing(15),
      margin: theme.spacing(1),
    },
    aboutContainer: {
      textAlign: "center",
      padding: theme.spacing(4),
    },
    aboutText: {
      color: "tan",
      marginTop: theme.spacing(2),
    },
  }));

  const About = () => {
    const classes = useStyles();

    return (
      <>
        <Particles /> 
        <Box className={classes.aboutContainer}>
          <Grid container justify="center">
            <Avatar className={classes.avatar} src={avatar} alt="Your Name" />
          </Grid>
          <Typography variant="h4" className={classes.aboutText}>
            Hello, I'm [Your Name]
          </Typography>
          <Typography variant="body1" className={classes.aboutText}>
            I'm a passionate software engineer with a love for creating meaningful
            applications that make a difference. My journey in the tech world has
            taught me the value of continuous learning and collaboration.
          </Typography>
          <Typography variant="body1" className={classes.aboutText}>
            Beyond coding, I enjoy [mention something personal or hobbies related
            to you]. I believe in maintaining a healthy work-life balance and
            nurturing both my professional and personal growth.
          </Typography>
        </Box>
      </>
    );
  };

  export default About;
